import "./App.css";
import { useEffect, useState } from "react";
import Post from "./components/Post";
import { set } from "date-fns";
import { BrowserRouter, Routes, Route, Navigate, Link, useNavigate, redirect } from "react-router-dom";
import Edit from "./components/Edit";
import Login from "./components/Login";
import { Redirect } from "react-router-dom";

function App() {
  const [posts, setPosts] = useState(null);
  const [user,setUser] = useState(false)

  async function fetchPosts() {
    await fetch("http://localhost:3000/posts")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setPosts(data);
      });
  }

  useEffect(() => {
    fetchPosts();
  }, []);

  return (
    <BrowserRouter>
    <div className="App">

 <Routes>
      <Route path={process.env.PUBLIC_URL + "/"} element={<Navigate to={process.env.PUBLIC_URL + "/login"} />} />
      <Route path={process.env.PUBLIC_URL + "/login"} element={<Login setUser={setUser}/>} />
      <Route path={process.env.PUBLIC_URL + "/post/create"} element={<Edit/>} />
      <Route path={process.env.PUBLIC_URL + "/posts"} element={     posts !== null &&  user &&  posts.map((post,i) => {
          return <Post post={post} key={i} fetchPosts={fetchPosts()}/>;
        })} />
    </Routes>
    </div>

    </BrowserRouter>
  );

}

export default App;
