import "./App.css";
import { useEffect, useState } from "react";
import Post from "./components/Post";
import { set } from "date-fns";

function App() {
  const [posts, setPosts] = useState(null);
  const [user,setUser] = useState(false)
  const [userName,setUserName] = useState('')
  const [password,setPassword] = useState('')
  const [loginError,setLoginError] = useState(false)

  useEffect(() => {
    async function fetchPosts() {
      await fetch("http://localhost:3000/posts")
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setPosts(data);
        });
    }

    fetchPosts();
  }, []);

  const handleSubmit = (event) => {
    event.target.reset();
    console.log("handleSubmit ran");
    event.preventDefault(); // üëàÔ∏è prevent page refresh

    // üëáÔ∏è access input values here
    console.log("Username üëâÔ∏è", userName);
    console.log("Password üëâÔ∏è", password);
    // üëáÔ∏è clear all input values in the form

    //very basic login verification
    if(userName==='admin' && password === 'dostoevsky') setUser(true)
    else setLoginError(true)
  };

  return (
    <div className="App">

      {
        !user &&           <form className="comment-form" onSubmit={handleSubmit}>
        <label htmlFor="username"></label>
        <input
          placeholder="Username"
          name="username"
          id="username"
          required
          onChange={(event) => setUserName(event.target.value)}
        ></input>
        <label htmlFor="password"></label>
        <input
        type={'password'}
          placeholder="Password"
          name="password"
          id="password"
          required
          onChange={(event) => setPassword(event.target.value)}
        ></input>
        {loginError && <div>Username or Password is incorrect ! </div>}
        <button type="submit">Login</button>
      </form>
      }

      {posts !== null && user &&
        posts.map((post,i) => {
          return <Post post={post} key={i} fetchPosts={fetchPosts()}/>;
        })}
    </div>
  );
}

export default App;
