//import "../styles/EditPost.css"
import { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

function EditPost(props) {
  const navigate = useNavigate()
  const params = useParams()

  const [title, setTitle] = useState("");
  const [content, setContent] = useState("");
  const [post, setPost] = useState(null);

  async function deletePost(){
    await fetch(
      `http://localhost:3000/post/${params.id}/delete`,
      {
        method: "DELETE"

      }
    );
    navigate(process.env.PUBLIC_URL + '/posts')
  }

  async function updatePost(title, content) {
    await fetch(
      `http://localhost:3000/post/${params.id}/update?title=${title}&content=${content}`,
      {
        method: "PUT",
      }
    );
    navigate(process.env.PUBLIC_URL + '/posts')
  }

  const handleSubmit = (event) => {
    event.target.reset();
    event.preventDefault(); // 👈️ prevent page refresh

    // 👇️ access input values here
    console.log("title 👉️", title);
    console.log("content 👉️", content);

    updatePost(title, content);
    // 👇️ clear all input values in the form
  };

  useEffect(() => {
    async function fetchPost() {
        await fetch(`http://localhost:3000/post/${params.id}`)
          .then((res) => {
            return res.json();
          })
          .then((data) => {
            console.log(data)
            setPost(data);
          });
      }
      fetchPost()
      //setTitle(post.title)
      //setContent(post.content)
  }, []);

  return (
    <div className="EditPost">
<form className="post-form" onSubmit={handleSubmit}>
            <label htmlFor="title"></label>
            <input
              placeholder="Title"
              name="title"
              required
              onChange={(event) => setTitle(event.target.value)}
            ></input>
            <label htmlFor="content"></label>
            <input
              placeholder="Write something..."
              name="content"
              required
              onChange={(event) => setContent(event.target.value)}
            ></input>
            <button type="submit">Submit</button>
          </form>
          <button onClick={()=>{deletePost()}}>Delete</button>
    </div>
  );
}

export default EditPost;
