import "./App.css";
import { useEffect, useState } from "react";
import Post from "./components/Post";
import { set } from "date-fns";
import { BrowserRouter, Routes, Route, Navigate, Link } from "react-router-dom";
import Edit from "./components/Edit";
import Login from "./components/Login";

function App() {
  const [posts, setPosts] = useState(null);
  const [user,setUser] = useState(false)
  const [userName,setUserName] = useState('')
  const [password,setPassword] = useState('')
  const [loginError,setLoginError] = useState(false)
  const [editMode, setEditMode] = useState(false)

  async function fetchPosts() {
    await fetch("http://localhost:3000/posts")
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        setPosts(data);
      });
  }

  useEffect(() => {
    fetchPosts();
  }, []);

  const handleSubmit = (event) => {
    event.target.reset();
    console.log("handleSubmit ran");
    event.preventDefault(); // üëàÔ∏è prevent page refresh

    // üëáÔ∏è access input values here
    console.log("Username üëâÔ∏è", userName);
    console.log("Password üëâÔ∏è", password);
    // üëáÔ∏è clear all input values in the form

    //very basic login verification
    if(userName==='admin' && password === 'dostoevsky') setUser(true)
    else setLoginError(true)
  };

  return (
    <BrowserRouter>
    <div className="App">

      {
        !user &&           <form className="comment-form" onSubmit={handleSubmit}>
        <label htmlFor="username"></label>
        <input
          placeholder="Username"
          name="username"
          id="username"
          required
          onChange={(event) => setUserName(event.target.value)}
        ></input>
        <label htmlFor="password"></label>
        <input
        type={'password'}
          placeholder="Password"
          name="password"
          id="password"
          required
          onChange={(event) => setPassword(event.target.value)}
        ></input>
        {loginError && <div>Username or Password is incorrect ! </div>}
        <button type="submit">Login</button>
      </form>
      }

 <Routes>
 <Route path={process.env.PUBLIC_URL + "/"} element={<Navigate to={process.env.PUBLIC_URL + "/login"} />} />
 <Route path={process.env.PUBLIC_URL + "/post/create"} element={<Login/>} />
      <Route path={process.env.PUBLIC_URL + "/post/create"} element={<Edit/>} />
      <Route path={process.env.PUBLIC_URL + "/posts"} element={     posts !== null &&    posts.map((post,i) => {
          return <Post post={post} key={i} fetchPosts={fetchPosts()}/>;
        })} />
    </Routes>
    </div>

    </BrowserRouter>
  );

}

export default App;
