  const [posts, setPosts] = useState(null);
  const [user,setUser] = useState(false)

  useEffect(() => {
    async function fetchPosts() {
      await fetch("http://localhost:3000/posts")
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          setPosts(data);
        });
    }

    fetchPosts();
  }, []);

