import { useEffect, useState } from "react";
import { format } from "date-fns";
import Comment from "../components/Comment";
import "../styles/Post.css";

function Post(props) {
  const [comments, setComments] = useState(null);
  const [commentsVisible, setCommentsVisible] = useState(false);
  const [update, setUpdate] = useState(0);

  async function fetchComments() {
    await fetch(`http://localhost:3000/post/${props.post._id}/comments`)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        console.log(data);
        setComments(data);
      });
  }

  useEffect(() => {
    fetchComments();
  }, []);

  useEffect(() => {
    console.log('update')
  }, [update]);

 async function postComment(name, content) {
    await fetch(
      `http://localhost:3000/post/${props.post._id}/comment/create?author=${name}&content=${content}`,
      {
        method: "POST", // or 'PUT'
        headers: {
          "Content-Type": "application/json",
        },
      }
    );
  }

  const [name, setName] = useState("");
  const [content, setContent] = useState("");

  const handleSubmit = (event) => {
    console.log("handleSubmit ran");
    event.preventDefault(); // üëàÔ∏è prevent page refresh

    // üëáÔ∏è access input values here
    console.log("name üëâÔ∏è", name);
    console.log("content üëâÔ∏è", content);

    postComment(name, content);
    // üëáÔ∏è clear all input values in the form
    setName("");
    setContent("");

    setUpdate(1)
  };

  return (
    <div className="Post">
      <div>
        <div className="title">{props.post.title}</div>
        <div className="date">
          <div>{format(new Date(props.post.date), "MMMM do, yyyy ")}</div>
          <div>{format(new Date(props.post.date), "H:mm")}</div>
        </div>
      </div>
      <div className="content">{props.post.content}</div>
      {!commentsVisible && (
        <button
          onClick={() => {
            setCommentsVisible(true);
          }}
        >
          show comments
        </button>
      )}
      <div className="comments">
        {commentsVisible &&
          comments.map((comment) => {
            return <Comment comment={comment} />;
          })}

        {commentsVisible && (
          <form className="comment-form" onSubmit={handleSubmit}>
            <label htmlFor="name"></label>
            <input
              placeholder="Name"
              name="name"
              id="name"
              required
              onChange={(event) => setName(event.target.value)}
            ></input>
            <label htmlFor="comment"></label>
            <input
              placeholder="Leave a comment..."
              name="comment"
              id="comment"
              required
              onChange={(event) => setContent(event.target.value)}
            ></input>
            <button>Submit</button>
          </form>
        )}

        {commentsVisible && (
          <button
            onClick={() => {
              setCommentsVisible(false);
            }}
          >
            hide comments
          </button>
        )}
      </div>
    </div>
  );
}

export default Post;
