import { useEffect, useState } from "react";
import { format } from "date-fns";
import Comment from "../components/Comment";
import "../styles/Post.css";

function Post(props) {
  const [comments, setComments] = useState(null);
  const [commentsVisible, setCommentsVisible] = useState(false);

  useEffect(() => {
    async function fetchComments() {
      await fetch(`http://localhost:3000/post/${props.post._id}/comments`)
        .then((res) => {
          return res.json();
        })
        .then((data) => {
          console.log(data);
          setComments(data);
        });
    }

    fetchComments();
  }, []);

  return (
    <div className="Post">
      <div>
        <div className="title">{props.post.title}</div>
        <div className="date">
          <div>{format(new Date(props.post.date), "MMMM do, yyyy ")}</div>
          <div>{format(new Date(props.post.date), "H:mm")}</div>
        </div>
      </div>
      <div className="content">{props.post.content}</div>
      {!commentsVisible && (
        <button
          onClick={() => {
            setCommentsVisible(true);
          }}
        >
          show comments
        </button>
      )}
      <div className="comments">
        {commentsVisible &&
          comments.map((comment) => {
            return <Comment comment={comment} />;
          })}

    {commentsVisible && <form>
        <input></input>
        <textarea ></textarea>
        </form>}

        {commentsVisible && (
          <button
            onClick={() => {
              setCommentsVisible(false);
            }}
          >
            hide comments
          </button>
        )}
      </div>
    </div>
  );
}

export default Post;
